import React, { useState } from 'react';

export const Many_Answers = () => {
        const type = "many";

        const [items, setValue] = useState([{
            id: 0,
            question_type: '',
            question_text: '',
            answer: []        
        }]);
    
        const additem = () => {
            var i = items.length;
            i += 1;
            var newitem = {
                id: i,
                question_type: '',
                question_text: '',
                answer: []
            };
            setValue((prevState) => [...prevState, newitem]);
            //Вывод для проверки
            console.log(i);
        };
        const deleteitem = (id) =>{
            var i = items.length;
            if(i > 1){
                setValue((prevState) => {
                    const idx = prevState.findIndex((item) => item.id === id);
                    return[...prevState.slice(0,idx), ...prevState.slice(idx+1)];
                });
                //Для проверки
                i--;
            } 
            else{ 
                alert('Нельзя удалить единственный вопрос!');
            };
            //Для проверки
            console.log(i);
        };
        
        return(
            <div className = "task_short_text">
                <h1>Текст вопроса</h1>
                <textarea id="short_text"></textarea>
            </div>
        )
}
