import React, { useState } from 'react';


export const ShablonApp = () => {

    //let i =0;
    const [items, setValue] = useState([{
        id: 0,
        question_type: '',
        question_text: '',
        answer: []
    }]);

   
    //Получаем текст и варианты ответов на вопросы
    const SetQuestion = (id, type) =>{
        var qtype = type;
        var qtext ='';
        var qans =[];
        var a, j;
        var inputs = document.getElementsByName('question_area_' + id);
        for (j = 0; j < inputs.length; j++) {
            a = inputs[j];
            qtext = a.value;
        }
        if((qtype !== 'short') || (qtype !== 'long')){
            inputs = document.getElementsByName('answer_area_' + id);
            for (j = 0; j < inputs.length; j++) {
                a = inputs[j];
                qans.push(a.value);
            }
        }
        var copy = Object.assign([], items);
        var idx = copy.findIndex((item) => item.id === id);
        var newitem={
            id: id,
            question_type: qtype,
            question_text: qtext,
            answer: qans
        };
        copy[idx]=newitem;
        setValue(copy);

        /*console.log(qtype);
        console.log(qtext);
        console.log(qans);*/
    };
    //Добавить запись в бд
    const SaveShablon = () => {};
        
    return(
        <div id = "main_block_c">
            
        </div>
    );
    
};
