import React, { useState } from 'react';
import QPool from './qpoll';
import {MainButtoms} from './mainbuttons';


export const App = () => {

    const [items, setValue] = useState([{
        id: 0,
        question_type: '',
        question_text: '',
        answer: []        
    }]);

    //Добавляем вопрос
    const additem = () => {
        let i = 0;
        for(var j=0;j<items.length;j++){
            if(items[j].id>i){
                i=items[j].id;
            }
        }
        i++;
        var newitem = {
            id: i,
            question_type: '',
            question_text: '',
            answer: []
        };
        setValue((prevState) => [...prevState, newitem]);
        //Вывод для проверки
        //console.log(i);
    };
    //Удаляем вопрос
    const deleteitem = (id) =>{
        var count = items.length;
        if(count > 1){
            setValue((prevState) => {
                const idx = prevState.findIndex((item) => item.id === id);
                return[...prevState.slice(0,idx), ...prevState.slice(idx+1)];
            });
            //Для проверки
            count--;
        } 
        else{ 
            alert('Нельзя удалить единственный вопрос!');
        };
        //Для проверки
        //console.log(count);
    };
    //Получаем текст и варианты ответов на вопросы
    const SetQuestion = (id, type) =>{
        var qtype = type;
        var qtext ='';
        var qans =[];
        var a, j;
        var inputs = document.getElementsByName('question_area_' + id);
        for (j = 0; j < inputs.length; j++) {
            a = inputs[j];
            qtext = a.value;
        }
        if((qtype !== 'short') || (qtype !== 'long')){
            inputs = document.getElementsByName('answer_area_' + id);
            for (j = 0; j < inputs.length; j++) {
                a = inputs[j];
                qans.push(a.value);
            }
        }
        var copy = Object.assign([], items);
        var idx = copy.findIndex((item) => item.id === id);
        var newitem={
            id: id,
            question_type: qtype,
            question_text: qtext,
            answer: qans
        };
        copy[idx]=newitem;
        setValue(copy);

        /*console.log(qtype);
        console.log(qtext);
        console.log(qans);*/
    };
    //Поиск
    //Добавить запись в бд
    const SaveShablon = () => {
        console.log('save');
    };
    //Новый шаблон-очистить поле создания шаблона+
    const NewShablon =()=>{
        var newitem = {
            id: 0,
            question_type: '',
            question_text: '',
            answer: []
        };
        setValue((prevState) => [newitem]);
        let title = document.getElementById('shablon_title');
        title.value="";
        let stype = document.getElementById("typeselect");
        stype.value="public";
        let slogo = document.getElementById("logoselect");
        slogo.value="OKB";
    };
    //Копировать шаблон
    const CopyShablon =()=>{

    };
    //Удалить шаблон
    const DeleteShablon =()=>{

    };
    //Копировать ссылку на шаблон
    const CopyLink =()=>{

    };
    //Редактировать шаблон
    const EditShablon =()=>{

    }

    return(
        <div id = "main_block_c">
            <div id="block1">
                <MainButtoms NewShablon={NewShablon}></MainButtoms>
            </div>
            <div id="block2">
                <div className = "mainp">
                    <h1>Название</h1>
                    <input type = "text" id = "shablon_title" name='Shablon_title'></input>
                    <h1>Тип опроса</h1>
                    <select id ="typeselect" name='shablontype'>
                        <option value="public">Публичный</option>
                        <option value="anon">Анонимный</option>
                    </select>
                        {
                            items.map((item) => {
                                return(
                                    <QPool 
                                    id={item.id} 
                                    key={item.id}
                                    image_delete={require('../images/close.png')}
                                    onDeleted={deleteitem}
                                    setInfo={SetQuestion}>
                                    </QPool>
                                );
                            })
                        }
                    
                    <button id="addtask" onClick={additem}>
                        <img src={require('../images/plus.png')} alt='add'></img>
                        <h1>Добавить вопрос</h1>
                    </button>
                    <button id="addtask" onClick={SaveShablon}>
                        <h1>Сохранить</h1>
                    </button>
                </div>
            </div>
        </div>
    );

};
