import React from 'react'

export const Short_Answer = () =>{
        const type = "short";
        const SetAnswer = () =>{
            this.props.qtype = this.type;
            var inputs = document.getElementsByName('answer_area_' + this.props.key);
            for (var i = 0; i < input.length; i++) {
                var a = input[i];
                this.props.qtext = a.value;
            }
        };
        return(
            <div className = "task_short_text">
                <h1>Текст вопроса</h1>
                <textarea id="short_text" name={'question_area_' + this.props.key} onfocusout={() => SetAnswer()}></textarea>
            </div>
        )
}
